/**
 * Check if a value is a constructor
 * @param value - The value to check
 * @returns True if the value is a constructor, false otherwise
 */
export function isConstructor(value: unknown): boolean;

/**
 * Check if a value is a constructor and not null
 * @param value - The value to check
 * @returns True if the value is a constructor and not null, false otherwise
 */
export function isNotNullConstructor(value: unknown): boolean;
