/**
 * Check if a value is extensible (can be extended by a class)
 * @param value - The value to check
 * @returns True if the value is extensible, false otherwise
 */
export function isExtensable(value: unknown): boolean;

/**
 * Check if a value is a constructor
 * @param value - The value to check
 * @returns True if the value is a constructor, false otherwise
 */
export function isConstructor(value: unknown): boolean;
